import React,{Component} from 'react';
import {Table} from 'react-bootstrap';

export class Employee extends Component {

    constructor(props){
        super(props);
        this.state={emps:[]}
    }

    componentDidMount(){
        this.refreshList();
    }

    refreshList(){
        fetch('')
        .then(Response=> Response.json())
        .then(data => {
        this.setState({emps:data});
        }
        );
    }


    render(){
        const {emps} = this.state;
        return(
            <Table className="mt-4" striped bordered hover size="sm">
                <thead>
                    <tr>
                        <th>EmployeeId</th>
                        <th>FullName</th>
                        <th>Email</th>
                        <th>Password</th>
                        <th>Mobile</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    {emps.map(emp=>
                        <tr key={emp.EmployeeId}>
                        <td> {emp.EmployeeId}</td>
                        <td> {emp.FullName}</td>
                        <td> {emp.Email}</td>
                        <td> {emp.Password}</td>
                        <td> {emp.Mobile}</td>
                        <td> {emp.Address}</td>
                        </tr>
                        )}
                </tbody>
            </Table>
        )
    }
}
