import React, {Component} from 'react';
import {Modal, Button, Row, Col, Form} from 'react-bootstrap';

import SnackBar from '@material-ui/core/SnackBar';
import IconButton from '@material-ui/core/IconButton'; 

export class AddEmployee extends Component{
    constructor(props){
        super(props);

        this.state = {snackbaropen: false,snackbarmsg:''};
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    snackbarClose = (event) =>{
        this.setState({snackbaropen:false});
    };

    handleSubmit(event){
        event.preventDefault();

        fetch('https://localhost:44369//api/Employee/AddEmployee',{
            method:'POST',
            headers:{
                'Accept': 'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                EmployeeId:null,
                FullName: event.target.FullName.value,
                Email: event.target.Email.value,
                Password:event.target.Password.value,
                Mobile:event.target.Mobile.value,
                Address:event.target.Email.value
            })
        })
        .then(res=> res.json())
        .then((result)=>
        {
            alert(result);
        },
        (error)=>{
            alert('Failed')
        }
        )

        // alert(event.target.FullName.value);
        // alert(event.target.Email.value);
        // alert(event.target.Password.value);
        // alert(event.target.Mobile.value);
        // alert(event.target.Address.value);

    }

    render(){
        return(
            <div className="container">

            <Modal
                {...this.props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
                >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                    Add  Employee
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                        <Row>
                            <Col sm={6}>
                                <Form onSubmit={this.handleSubmit}>
                                    <Form.Group controlId="FullName">
                                        <Form.Label>FullName</Form.Label>
                                        <Form.Control
                                        type="text"
                                        name="FullName"
                                        required
                                        placeholder="FullName"

                                        />
                                    </Form.Group>
                                    <Form.Group controlId="Email">
                                        <Form.Label>Email Id</Form.Label>
                                        <Form.Control
                                        type="text"
                                        name="Email"
                                        required
                                        placeholder="Email"

                                        />
                                    </Form.Group>
                                    <Form.Group controlId="Password">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control
                                        type="text"
                                        name="Password"
                                        required
                                        placeholder="Password"

                                        />
                                    </Form.Group>
                                    <Form.Group controlId="Mobile">
                                        <Form.Label>Mobile no.</Form.Label>
                                        <Form.Control
                                        type="text"
                                        name="Mobile"
                                        required
                                        placeholder="Mobile"

                                        />
                                    </Form.Group>
                                    <Form.Group controlId="Address">
                                        <Form.Label>Address</Form.Label>
                                        <Form.Control
                                        type="text"
                                        name="Address"
                                        required
                                        placeholder="Address"

                                        />
                                    </Form.Group>
                                    <Form.Group>
                                     <Button variant="primary" type="submit">
                                         Add Employee</Button>   
                                    </Form.Group>
                                </Form>
                            </Col>
                        </Row>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.props.onHide}>Close</Button>
                </Modal.Footer>
                </Modal>
                </div>

        )
    }
}
